# Core Packages
import streamlit as st  

# Work with Media Files(videos/images/audio)
# Display Images
from PIL import Image
img = Image.open("image_ex1.jpg")
st.image(img)
# With size adjustments
img2 = Image.open("image_02.jpg")
st.image(img2,use_column_width=True)

# From URL
st.image("data:image/jpeg;base64,")

# Display Videos
video_file = open("mp4_ex1.mp4","rb").read()
st.video(video_file, start_time=3)

# Display Audio/Working with Audio
audio_file = open("mp3_ex1.mp3","rb")
st.audio(audio_file.read(),format='audio/mp3')
